import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Calendar.css'
import { Card, CardContent, Typography } from '@mui/material';
import { styled } from '@mui/system';

const StyledResultsList = styled('ul')({
    listStyle: 'none',
    padding: 0,
});

const StyledResultItem = styled('li')({
    border: '1px solid #ddd',
    marginBottom: '10px',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
    overflow: 'hidden',
});

const StyledScore = styled('div')({
    display: 'flex',
    justifyContent: 'space-between',
    padding: '10px',
    backgroundColor: '#2196f3',
    color: '#fff',
});

const StyledDate = styled(Typography)({
    textAlign: 'center',
    padding: '10px',
});


interface Match {
    date: string;
    home_team_score: number;
    visitor_team_score: number;
    season: string;
    home_team: Team;
    visitor_team: Team;
}

interface Team {
    id: number;
    full_name: string;
    name: string;
    abbreviation: string;
    city: string;
    conference: string;
    division: string;
    // Ajoutez d'autres propriétés si nécessaire
}


async function getMatchs(teamId: string): Promise<Match[]> {
    const options = {
        method: 'GET',
        url: 'https://www.balldontlie.io/api/v1/games',
        params: {
            team_ids: [teamId],
            page: '0',
            per_page: '30'
        },
    };
    try {
        const response = await axios.request(options);
        return response.data.data;
    } catch (error) {
        console.error(error);
        return [];
    }
}

function Calendar({ teamId }: { teamId: string }) {
    const [matchs, setMatchs] = useState<Match[]>([]);

    useEffect(() => {
        async function fetchData() {
            const matchsData = await getMatchs(teamId);
            setMatchs(matchsData);
        }
        fetchData();
    }, []);

    const DisplayData = matchs.map((match) => (
        <StyledResultItem key={match.date}>
            <StyledScore>
                <Typography variant="h6">{match.home_team_score}</Typography>
                <Typography variant="h6">{match.visitor_team_score}</Typography>
            </StyledScore>
            <CardContent>
                <Typography variant="subtitle1">{match.home_team.name}</Typography>
                <Typography variant="subtitle1">{match.visitor_team.name}</Typography>
            </CardContent>
            <StyledDate variant="body2">{match.date}</StyledDate>
        </StyledResultItem>
    ));
    if (!teamId) {
        // Gérer le cas où teamId est undefined
        return <div>Équipe non spécifiée</div>;
    }
    return (
        <StyledResultsList>{DisplayData}</StyledResultsList>
    )
}

export default Calendar;
