import axios from 'axios';
import React, { useEffect, useState } from 'react';
import ATL from '../img/teams/ATL.png'
interface Team {
    id: number;
    full_name: string;
    abbreviation: string;
    city: string;
    conference: string;
    division: string;
    // Ajoutez d'autres propriétés si nécessaire
}

async function GetTeams(): Promise<Team[]> {
    const options = {
        method: 'GET',
        url: 'https://www.balldontlie.io/api/v1/teams',
        params: {
            page: '0',
            per_page: '30'
        },
    };
    try {
        const response = await axios.request(options);
        return response.data.data;
    } catch (error) {
        console.error(error);
        return [];
    }
}

function importAll(r: any) {
    let images: { [key: string]: any } = {};
    r.keys().forEach((item: string, index: number) => {
        images[item.replace('./', '')] = r(item);
    });
    return images;
}

const images = importAll(require.context('../assets', false, /\.(png|jpe?g|svg)$/));

function Team_table() {
    const [teams, setTeams] = useState<Team[]>([]);

    useEffect(() => {
        async function fetchData() {
            const teamsData = await GetTeams();
            setTeams(teamsData);
        }

        fetchData();
    }, []);

    const DisplayData = teams.map((team) => (
        <div key={team.id}>
            <img src={require(`../img/teams/${team.abbreviation}.png`)} alt={team.abbreviation} />
            <li>
                <strong>{team.full_name}</strong> -
                Abbréviation : {team.abbreviation},
                Ville : {team.city},
                Conférence : {team.conference},
                Division : {team.division}
            </li>
        </div>
    ));

    return (
        <div>{DisplayData}</div>
    )
}

export default Team_table;
