import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface Match {
    date: string;
    home_team_score: number;
    visitor_team_score: number;
    season: string;
    home_team: Team;
    visitor_team: Team;
}

interface Team {
    id: number;
    full_name: string;
    name: string;
    abbreviation: string;
    city: string;
    conference: string;
    division: string;
    // Ajoutez d'autres propriétés si nécessaire
}

interface CalendarProps {
    teamId: string;
}

async function getMatchs(teamId: string): Promise<Match[]> {
    const options = {
        method: 'GET',
        url: 'https://www.balldontlie.io/api/v1/games',
        params: {
            team_ids: [teamId],
            page: '0',
            per_page: '30'
        },
    };
    try {
        const response = await axios.request(options);
        return response.data.data;
    } catch (error) {
        console.error(error);
        return [];
    }
}

function Calendar({ teamId }: CalendarProps) {
    const [matchs, setMatchs] = useState<Match[]>([]);

    useEffect(() => {
        async function fetchData() {
            const matchsData = await getMatchs(teamId);
            setMatchs(matchsData);
        }
        fetchData();
    }, []);

    const DisplayData = matchs.map((match) => (
        <div>
            <p> date : {match.date}, {match.home_team.name} - {match.home_team_score} : {match.visitor_team.name} - {match.visitor_team_score} </p>
        </div>
    ));

    return (
        <div className="team-container">{DisplayData}</div>
    )
}

export default Calendar;
