import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Calendar.css'
import Stack from '@mui/material/Stack';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import { styled } from '@mui/material/styles';
import Grid from '@mui/material/Grid';



interface Match {
    date: string;
    home_team_score: number;
    visitor_team_score: number;
    season: string;
    home_team: Team;
    visitor_team: Team;
}

interface Team {
    id: number;
    full_name: string;
    name: string;
    abbreviation: string;
    city: string;
    conference: string;
    division: string;
    // Ajoutez d'autres propriétés si nécessaire
}

const Item = styled('div')(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    border: '1px solid',
    borderColor: theme.palette.mode === 'dark' ? '#444d58' : '#ced7e0',
    padding: theme.spacing(1),
    borderRadius: '4px',
    textAlign: 'center',
}));

async function getMatchs(teamId: string): Promise<Match[]> {
    const options = {
        method: 'GET',
        url: 'https://www.balldontlie.io/api/v1/games',
        params: {
            team_ids: [teamId],
            page: '0',
            per_page: '30'
        },
    };
    try {
        const response = await axios.request(options);
        return response.data.data;
    } catch (error) {
        console.error(error);
        return [];
    }
}

function Calendar({ teamId }: { teamId: string }) {
    const [matchs, setMatchs] = useState<Match[]>([]);

    useEffect(() => {
        async function fetchData() {
            const matchsData = await getMatchs(teamId);
            setMatchs(matchsData);
        }
        fetchData();
    }, []);



    const DisplayData = matchs.map((match) => (
        <div key={match.date}>
            <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={3} columns={16}>
                    <Grid xs={4}>
                        <Item>{match.home_team.name} </Item>
                    </Grid>
                    <Grid xs={6}>
                        <Item>{match.home_team_score} - {match.visitor_team_score} </Item>
                    </Grid>
                    <Grid xs={6}>
                        <Item>{match.visitor_team.name}</Item>
                    </Grid>
                </Grid>
            </Box>
        </div>
    ));
    if (!teamId) {
        // Gérer le cas où teamId est undefined
        return <div>Équipe non spécifiée</div>;
    }
    return (
        <div>{DisplayData}

        </div>


    )
}

export default Calendar;
